mapper.insert:
insert into job_record (id, record_name, record_type, 
  delete_flg, create_time, create_user_id, 
  update_time, update_user_id, record_content,owner_id,owner_name
  )
values (#{id,jdbcType=BIGINT}, #{recordName,jdbcType=VARCHAR}, #{recordType,jdbcType=INTEGER}, 
  #{deleteFlg,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, #{createUserId,jdbcType=INTEGER}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{updateUserId,jdbcType=INTEGER}, #{recordContent,jdbcType=LONGVARCHAR},
  #{ownerId,jdbcType=INTEGER},#{ownerName,jdbcType=VARCHAR}
  )

mapper.select:
select

,

from job_record


    and id=#{id}


    and record_type = #{recordType,jdbcType=INTEGER}


    and delete_flg= #{deleteFlg,jdbcType=INTEGER}


ORDER BY id DESC
